import React, { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import axios from "axios";
import styled from "styled-components";
import { FaNewspaper } from "react-icons/fa"; // Importing an icon for the logo

const Container = styled.div`
  min-height: 100vh;
  background-color: #f3f4f6;
  padding: 24px;
`;

const Navbar = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  background-color: white;
  padding: 16px 24px;
  border-bottom: 2px solid #e5e7eb;
`;

const LogoContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 1.5rem;
  font-weight: bold;
  color: #2563eb;
`;

const NavLinksContainer = styled.div`
  display: flex;
  gap: 16px;

  @media (max-width: 768px) {
    display: none; /* Hide categories on smaller screens */
  }
`;

const NavButton = styled.button<{ $active?: boolean }>`
  padding: 8px 12px;
  border-radius: 6px;
  color: #fff;
  background-color: ${(props) => (props.$active ? "#2563eb" : "#6b7280")};
  cursor: pointer;
  border: none;
  transition: background-color 0.3s;

  &:hover {
    background-color: ${(props) => (props.$active ? "#1d4ed8" : "#4b5563")};
  }
`;

const SearchBox = styled.input`
  padding: 8px;
  width: 220px;
  border-radius: 6px;
  border: 1px solid #d1d5db;
  outline: none;
  transition: border-color 0.3s;

  &:focus {
    border-color: #2563eb;
  }
`;

const NewsAggregator: React.FC = () => {
  const [category, setCategory] = useState<string>(
    () => localStorage.getItem("newsCategory") || "technology"
  );
  const [searchTerm, setSearchTerm] = useState<string>(
    () => localStorage.getItem("newsSearch") || ""
  );

  const handleCategoryClick = (newCategory: string) => {
    if (newCategory !== category) {
      setCategory(newCategory);
      localStorage.setItem("newsCategory", newCategory);
    }
  };

  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
    localStorage.setItem("newsSearch", e.target.value);
  };

  return (
    <Container>
      {/* Navbar */}
      <Navbar>
        <LogoContainer>
          <FaNewspaper size={24} />
          News Aggregator
        </LogoContainer>

        {/* Categories */}
        <NavLinksContainer>
          {["technology", "business", "entertainment", "health", "science", "sports"].map(
            (cat) => (
              <NavButton
                key={cat}
                onClick={() => handleCategoryClick(cat)}
                $active={category === cat}
              >
                {cat.charAt(0).toUpperCase() + cat.slice(1)}
              </NavButton>
            )
          )}
        </NavLinksContainer>

        {/* Search Box */}
        <SearchBox
          type="text"
          placeholder="Search news..."
          value={searchTerm}
          onChange={handleSearchChange}
        />
      </Navbar>

      {/* Other News Content */}
      <h2 style={{ textAlign: "center", marginTop: "20px" }}>
        News Category: {category.charAt(0).toUpperCase() + category.slice(1)}
      </h2>
    </Container>
  );
};

export default NewsAggregator;
