import { useState } from "react";
import axios from "axios";

const NewsAggregator = () => {
  const [newsText, setNewsText] = useState("");
  type ModelResult = { label: string; score: number }; // Define expected structure
  const [result, setResult] = useState<ModelResult | null>(null);


  const checkFakeNews = async () => {
    try {
      const response = await axios.post("http://localhost:8000/predict/", {
        news_text: newsText,
      });
      setResult(response.data);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div className="container">
      <h2>Fake News Detector</h2>
      <textarea 
        value={newsText} 
        onChange={(e) => setNewsText(e.target.value)}
        placeholder="Enter news text here..."
      />
      <button onClick={checkFakeNews}>Check News</button>

      {result && (
        <div>
          <h3>Result:</h3>
          <button style={{ backgroundColor: result.label === "LABEL_0" ? "red" : "green" }}>
            {result.label === "LABEL_0" ? "Fake News" : "Real News"}
          </button>
        </div>
      )}
    </div>
  );
};

export default NewsAggregator;
