import React, { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import axios from "axios";
import styled from "styled-components";
import { FaArrowLeft, FaArrowRight } from "react-icons/fa";

// Styled Components
const Container = styled.div`
  max-width: 800px;
  margin: auto;
  padding: 20px;
  background: #f8f9fa;
  border-radius: 10px;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.1);
`;

const Title = styled.h1`
  text-align: center;
  color: #2563eb;
  margin-bottom: 20px;
`;

const SearchContainer = styled.div`
  display: flex;
  justify-content: center;
  margin-bottom: 20px;
`;

const Input = styled.input`
  padding: 10px;
  width: 300px;
  border: 2px solid #ddd;
  border-radius: 5px;
  margin-right: 10px;
`;

const Button = styled.button`
  padding: 10px 15px;
  background-color: #2563eb;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: background 0.3s;

  &:hover {
    background-color: #1d4ed8;
  }
`;

const NewsCard = styled.div`
  background: white;
  padding: 15px;
  margin-bottom: 15px;
  border-radius: 8px;
  box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.1);
`;

const NewsTitle = styled.h2`
  font-size: 18px;
  color: #333;
  margin-bottom: 5px;
`;

const Source = styled.p`
  font-size: 14px;
  color: #777;
`;

const Link = styled.a`
  color: #2563eb;
  text-decoration: none;
  font-weight: bold;

  &:hover {
    text-decoration: underline;
  }
`;

const PaginationContainer = styled.div`
  display: flex;
  justify-content: center;
  gap: 12px;
  margin-top: 20px;
`;

const PageButton = styled.button`
  background-color: ${(props) => (props.disabled ? "#ccc" : "#2563eb")};
  color: white;
  padding: 8px 12px;
  border: none;
  border-radius: 5px;
  cursor: ${(props) => (props.disabled ? "not-allowed" : "pointer")};
  transition: background 0.3s;

  &:hover {
    background-color: ${(props) => (props.disabled ? "#ccc" : "#1d4ed8")};
  }
`;

// Fetch News Function
const fetchNews = async ({ queryKey }: { queryKey: [string, string, string, number] }) => {
  const [, category, searchTerm, currentPage] = queryKey;
  const BACKEND_URL =
    process.env.NODE_ENV === "development"
      ? "http://localhost:5001"
      : process.env.REACT_APP_BACKEND_URL;

  const url = searchTerm
    ? `${BACKEND_URL}/api/news?search=${searchTerm}&page=${currentPage}`
    : `${BACKEND_URL}/api/news?category=${category}&page=${currentPage}`;

  const response = await axios.get(url);
  return response.data.articles || [];
};

const NewsAggregator: React.FC = () => {
  const [category, setCategory] = useState("technology");
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);

  const { data: news = [], isLoading: loading, error } = useQuery({
    queryKey: ["news", category, searchTerm, currentPage],
    queryFn: fetchNews,
    staleTime: 5 * 60 * 1000,
    refetchOnWindowFocus: false,
  });

  return (
    <Container>
      <Title>AI News Aggregator</Title>

      {/* Search Input */}
      <SearchContainer>
        <Input
          type="text"
          placeholder="Search news..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <Button onClick={() => setCurrentPage(1)}>Search</Button>
      </SearchContainer>

      {/* News Content */}
      {error ? (
        <p style={{ textAlign: "center" }}>{error.message}</p>
      ) : loading ? (
        <p style={{ textAlign: "center" }}>Loading news...</p>
      ) : (
        <>
          {news.map((article: any, index: number) => (
            <NewsCard key={index}>
              <NewsTitle>{article.title}</NewsTitle>
              <Source>{article.source.name}</Source>
              <Link href={article.url} target="_blank" rel="noopener noreferrer">
                Read more
              </Link>
            </NewsCard>
          ))}

          {/* Pagination */}
          <PaginationContainer>
            <PageButton
              onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
              disabled={currentPage === 1}
            >
              <FaArrowLeft /> Previous
            </PageButton>
            <span>Page {currentPage}</span>
            <PageButton onClick={() => setCurrentPage((prev) => prev + 1)} disabled={news.length === 0}>
              Next <FaArrowRight />
            </PageButton>
          </PaginationContainer>
        </>
      )}
    </Container>
  );
};

export default NewsAggregator;
