import React, { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import axios from "axios";
import styled, { ThemeProvider, createGlobalStyle } from "styled-components";
import { FaNewspaper, FaSearch, FaMoon, FaSun } from "react-icons/fa";

// Global Styles
const GlobalStyle = createGlobalStyle`
  body {
    background-color: ${(props: any) => props.theme.background};
    color: ${(props: any) => props.theme.color};
    transition: background-color 0.3s, color 0.3s;
  }
`;

// Themes
const lightTheme = {
  background: "#f3f4f6",
  color: "#1f2937",
  navbarBackground: "#ffffff",
  buttonBackground: "#2563eb",
  buttonHoverBackground: "#1d4ed8",
  shadow: "rgba(0, 0, 0, 0.1)",
};

const darkTheme = {
  background: "#1f2937",
  color: "#f3f4f6",
  navbarBackground: "#374151",
  buttonBackground: "#6b7280",
  buttonHoverBackground: "#4b5563",
  shadow: "rgba(255, 255, 255, 0.1)",
};

// Styled components
const Container = styled.div`
  min-height: 100vh;
  padding: 24px;
`;

const Navbar = styled.nav`
  display: flex;
  align-items: center;
  justify-content: space-between;
  background-color: ${(props: any) => props.theme.navbarBackground};
  padding: 12px 24px;
  border-radius: 8px;
  box-shadow: 0 2px 10px ${(props: any) => props.theme.shadow};
  margin-bottom: 24px;
`;

const Logo = styled.div`
  display: flex;
  align-items: center;
  font-size: 1.5rem;
  font-weight: bold;
  color: ${(props: any) => props.theme.buttonBackground};

  svg {
    margin-right: 8px;
  }
`;

const NavLinks = styled.div`
  display: flex;
  align-items: center;
  gap: 12px;
`;

const NavButton = styled.button<{ $active?: boolean }>`
  padding: 8px 12px;
  border-radius: 8px;
  color: #fff;
  background-color: ${(props) =>
    props.$active ? props.theme.buttonBackground : props.theme.shadow};
  cursor: pointer;
  border: none;
  transition: background-color 0.3s;

  &:hover {
    background-color: ${(props) =>
      props.$active ? props.theme.buttonHoverBackground : props.theme.shadow};
  }
`;

const SearchBox = styled.input`
  padding: 8px;
  width: 200px;
  border-radius: 8px;
  border: 1px solid #d1d5db;
  outline: none;
  transition: border-color 0.3s;

  &:focus {
    border-color: #2563eb;
  }
`;

const SearchIconContainer = styled.div`
  padding: 8px;
  border-radius: 50%;
  background-color: ${(props: any) => props.theme.buttonBackground};
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: ${(props: any) => props.theme.buttonHoverBackground};
  }

  svg {
    color: #fff;
    font-size: 1.2rem;
  }
`;

const ToggleIconContainer = styled.div`
  padding: 8px;
  border-radius: 50%;
  background-color: ${(props: any) => props.theme.buttonBackground};
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: ${(props: any) => props.theme.buttonHoverBackground};
  }

  svg {
    color: #fff;
    font-size: 1.2rem;
  }
`;

// NewsAggregator Component
const NewsAggregator: React.FC = () => {
  const [category, setCategory] = useState("technology");
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [showSearchInput, setShowSearchInput] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(false);

  const handleCategoryButtonClick = (newCategory: string) => {
    if (newCategory !== category) {
      setCategory(newCategory);
      setCurrentPage(1);
    }
  };

  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
  };

  const toggleTheme = () => {
    setIsDarkMode((prev) => !prev);
  };

  return (
    <ThemeProvider theme={isDarkMode ? darkTheme : lightTheme}>
      <GlobalStyle />
      <Container>
        <Navbar>
          <Logo>
            <FaNewspaper size={24} />
            News Aggregator
          </Logo>
          <NavLinks>
            {["technology", "business", "entertainment", "health", "science", "sports"].map((cat) => (
              <NavButton key={cat} onClick={() => handleCategoryButtonClick(cat)} $active={category === cat}>
                {cat.charAt(0).toUpperCase() + cat.slice(1)}
              </NavButton>
            ))}
            {showSearchInput ? (
              <SearchBox
                type="text"
                placeholder="Search..."
                value={searchTerm}
                onChange={handleSearchChange}
                onBlur={() => setShowSearchInput(false)}
              />
            ) : (
              <SearchIconContainer onClick={() => setShowSearchInput(true)}>
                <FaSearch />
              </SearchIconContainer>
            )}
            <ToggleIconContainer onClick={toggleTheme}>
              {isDarkMode ? <FaMoon /> : <FaSun />}
            </ToggleIconContainer>
          </NavLinks>
        </Navbar>
        {/* Add your remaining components and logic */}
      </Container>
    </ThemeProvider>
  );
};

export default NewsAggregator;
