import React, { useState } from "react";
import styled from "styled-components";
import { useQuery } from "react-query";

const Container = styled.div`
  max-width: 800px;
  margin: auto;
  padding: 20px;
`;

const SearchInput = styled.input`
  width: 100%;
  padding: 10px;
  margin-bottom: 20px;
  border-radius: 5px;
  border: 1px solid #ccc;
`;

const NewsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 20px;
  margin-top: 20px;
`;

const NewsCard = styled.div`
  background: #fff;
  padding: 15px;
  border-radius: 8px;
  box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.1);
`;

const FakeNewsLabel = styled.span<{ fake: boolean }>`
  color: ${props => (props.fake ? "red" : "green")};
  font-weight: bold;
`;

// Define the NewsArticle type
interface NewsArticle {
  title: string;
  url: string;
  urlToImage?: string;
  description: string;
  source: { name: string };
  isFake: boolean;
}

// Define fetch function
const fetchNews = async ({ queryKey }: { queryKey: [string, string] }) => {
  const [, searchTerm] = queryKey;
  const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:5001";
  const url = searchTerm ? `${BACKEND_URL}/api/news?search=${searchTerm}` : `${BACKEND_URL}/api/news`;

  const response = await fetch(url);
  if (!response.ok) throw new Error("Failed to fetch news");
  return response.json();
};

const NewsAggregator = () => {
  const [searchTerm, setSearchTerm] = useState("");

  // Fetch news using react-query
  const { data: news = [], isLoading, error } = useQuery<NewsArticle[]>(
    ["news", searchTerm],
    fetchNews
  );

  return (
    <Container>
      <h1>AI News Aggregator</h1>
      <SearchInput
        type="text"
        placeholder="Search news..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      {isLoading ? (
        <p>Loading news...</p>
      ) : error ? (
        <p>Error fetching news</p>
      ) : (
        <NewsGrid>
          {news.map((article: NewsArticle, index: number) => (
            <NewsCard key={index}>
              {article.urlToImage && (
                <img src={article.urlToImage} alt={article.title} style={{ width: "100%", borderRadius: "6px" }} />
              )}
              <h2>{article.title}</h2>
              <p>{article.source.name}</p>
              <p>{article.description}</p>
              <FakeNewsLabel fake={article.isFake}>
                {article.isFake ? "⚠️ Fake News" : "✅ Real News"}
              </FakeNewsLabel>
              <a href={article.url} target="_blank" rel="noopener noreferrer">Read more</a>
            </NewsCard>
          ))}
        </NewsGrid>
      )}
    </Container>
  );
};

export default NewsAggregator;
