import React, { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import axios from "axios";
import styled from "styled-components";
import { FaArrowLeft, FaArrowRight } from "react-icons/fa";

// Styled Components for Pagination
const PaginationContainer = styled.div`
  display: flex;
  justify-content: center;
  gap: 12px;
  margin-top: 20px;
`;

const PageButton = styled.button`
  background-color: ${(props) => (props.disabled ? "#ccc" : "#2563eb")};
  color: white;
  padding: 8px 12px;
  border: none;
  border-radius: 5px;
  cursor: ${(props) => (props.disabled ? "not-allowed" : "pointer")};
  transition: background 0.3s;

  &:hover {
    background-color: ${(props) => (props.disabled ? "#ccc" : "#1d4ed8")};
  }
`;

// Fetch News Function
const fetchNews = async ({ queryKey }: { queryKey: [string, string, string, number] }) => {
  const [, category, searchTerm, currentPage] = queryKey;
  const BACKEND_URL =
    process.env.NODE_ENV === "development"
      ? "http://localhost:5001"
      : process.env.REACT_APP_BACKEND_URL;

  const url = searchTerm
    ? `${BACKEND_URL}/api/news?search=${searchTerm}&page=${currentPage}`
    : `${BACKEND_URL}/api/news?category=${category}&page=${currentPage}`;

  const response = await axios.get(url);
  return response.data.articles || [];
};

const NewsAggregator: React.FC = () => {
  const [category, setCategory] = useState("technology");
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);

  const { data: news = [], isLoading: loading, error } = useQuery({
    queryKey: ["news", category, searchTerm, currentPage],
    queryFn: fetchNews,
    staleTime: 5 * 60 * 1000,
    refetchOnWindowFocus: false,
  });

  return (
    <div>
      {error ? (
        <p style={{ textAlign: "center" }}>{error.message}</p>
      ) : loading ? (
        <p style={{ textAlign: "center" }}>Loading news...</p>
      ) : (
        <>
          <div>
            {news.map((article: any, index: number) => (
              <div key={index}>
                <h2>{article.title}</h2>
                <p>{article.source.name}</p>
                <a href={article.url} target="_blank" rel="noopener noreferrer">
                  Read more
                </a>
              </div>
            ))}
          </div>

          {/* Pagination Buttons */}
          <PaginationContainer>
            <PageButton
              onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
              disabled={currentPage === 1}
            >
              <FaArrowLeft /> Previous
            </PageButton>
            <span>Page {currentPage}</span>
            <PageButton onClick={() => setCurrentPage((prev) => prev + 1)} disabled={news.length === 0}>
              Next <FaArrowRight />
            </PageButton>
          </PaginationContainer>
        </>
      )}
    </div>
  );
};

export default NewsAggregator;
