import React, { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import axios from "axios";
import "./NewsAggregator.css";
import { FaArrowLeft, FaArrowRight } from "react-icons/fa";

const fetchNews = async ({ queryKey }) => {
  const [, category, searchTerm, currentPage] = queryKey;
  const BACKEND_URL =
    process.env.NODE_ENV === "development"
      ? "http://localhost:5001"
      : process.env.REACT_APP_BACKEND_URL;

  const url = searchTerm
    ? `${BACKEND_URL}/api/news?search=${searchTerm}&page=${currentPage}`
    : `${BACKEND_URL}/api/news?category=${category}&page=${currentPage}`;

  const response = await axios.get(url);
  return response.data.articles || [];
};

const NewsAggregator = () => {
  const [category, setCategory] = useState("technology");
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);

  const { data: news = [], isLoading: loading, error } = useQuery({
    queryKey: ["news", category, searchTerm, currentPage],
    queryFn: fetchNews,
    staleTime: 5 * 60 * 1000,
    refetchOnWindowFocus: false,
  });

  return (
    <div className="news-container">
      <h1 className="news-title">AI News Aggregator</h1>
      <div className="search-container">
        <input
          type="text"
          className="search-input"
          placeholder="Search news..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button className="search-button" onClick={() => setCurrentPage(1)}>
          Search
        </button>
      </div>
      {error ? (
        <p className="error-message">{error.message}</p>
      ) : loading ? (
        <p className="loading-message">Loading news...</p>
      ) : (
        <>
          <div className="news-list">
            {news.map((article, index) => (
              <div key={index} className="news-card">
                <h2 className="news-card-title">{article.title}</h2>
                <p className="news-card-source">{article.source.name}</p>
                <a
                  className="news-card-link"
                  href={article.url}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  Read more
                </a>
              </div>
            ))}
          </div>
          <div className="pagination-container">
            <button
              className="pagination-button"
              onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
              disabled={currentPage === 1}
            >
              <FaArrowLeft /> Previous
            </button>
            <span className="pagination-text">Page {currentPage}</span>
            <button
              className="pagination-button"
              onClick={() => setCurrentPage((prev) => prev + 1)}
              disabled={news.length === 0}
            >
              Next <FaArrowRight />
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default NewsAggregator;
