import React, { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import axios from "axios";
import styled from "styled-components";
import { FaNewspaper } from "react-icons/fa";

// Styled components
const Container = styled.div`padding: 20px; background: #f9f9f9; min-height: 100vh;`;
const Navbar = styled.nav`display: flex; justify-content: space-between; padding: 10px; background: #fff; box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);`;
const Logo = styled.div`display: flex; align-items: center; font-size: 24px; font-weight: bold; color: #2563eb;`;
const SearchBox = styled.input`padding: 8px; width: 250px; border: 1px solid #ddd; border-radius: 6px; outline: none;`;
const NewsGrid = styled.div`display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 20px; margin-top: 20px;`;
const NewsCard = styled.div`background: #fff; padding: 15px; border-radius: 8px; box-shadow: 0px 2px 5px rgba(0, 0, 0, 0.1);`;
const FakeNewsLabel = styled.span`color: ${props => (props.fake ? "red" : "green")}; font-weight: bold;`;

const fetchNews = async ({ queryKey }) => {
  const [, searchTerm] = queryKey;
  const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "http://localhost:5001";
  const url = searchTerm ? `${BACKEND_URL}/api/news?search=${searchTerm}` : `${BACKEND_URL}/api/news`;
  const response = await axios.get(url);
  return response.data.articles || [];
};

const NewsAggregator = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const { data: news = [], isLoading, error } = useQuery(["news", searchTerm], fetchNews);

  return (
    <Container>
      <Navbar>
        <Logo><FaNewspaper size={24} /> News Aggregator</Logo>
        <SearchBox type="text" placeholder="Search..." value={searchTerm} onChange={e => setSearchTerm(e.target.value)} />
      </Navbar>

      {error && <p style={{ color: "red" }}>Error fetching news.</p>}
      {isLoading ? <p>Loading news...</p> : (
        <NewsGrid>
          {news.map((article, index) => (
            <NewsCard key={index}>
              {article.urlToImage && <img src={article.urlToImage} alt={article.title} style={{ width: "100%", borderRadius: "6px" }} />}
              <h2>{article.title}</h2>
              <p>{article.source.name}</p>
              <p>{article.description}</p>
              <FakeNewsLabel fake={article.isFake}>{article.isFake ? "⚠️ Fake News" : "✅ Real News"}</FakeNewsLabel>
              <a href={article.url} target="_blank" rel="noopener noreferrer">Read more</a>
            </NewsCard>
          ))}
        </NewsGrid>
      )}
    </Container>
  );
};

export default NewsAggregator;
