import React, { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import axios from "axios";
import styled, { ThemeProvider, createGlobalStyle } from "styled-components";
import { FaNewspaper, FaSearch, FaMoon, FaSun } from "react-icons/fa";

// Global Styles
const GlobalStyle = createGlobalStyle`
  body {
    background-color: ${(props: any) => props.theme.background};
    color: ${(props: any) => props.theme.color};
    transition: background-color 0.3s, color 0.3s;
  }
`;

// Themes
const lightTheme = {
  background: "#f3f4f6",
  color: "#1f2937",
  navbarBackground: "#ffffff",
  buttonBackground: "#2563eb",
  buttonHoverBackground: "#1d4ed8",
  shadow: "rgba(0, 0, 0, 0.1)",
};

const darkTheme = {
  background: "#1f2937",
  color: "#f3f4f6",
  navbarBackground: "#374151",
  buttonBackground: "#6b7280",
  buttonHoverBackground: "#4b5563",
  shadow: "rgba(255, 255, 255, 0.1)",
};

// Styled component for the toggle button
const ToggleIconContainer = styled.div`
  padding: 8px;
  border-radius: 50%;
  background-color: ${(props: any) => props.theme.buttonBackground};
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: ${(props: any) => props.theme.buttonHoverBackground};
  }

  svg {
    color: #fff;
    font-size: 1.2rem;
  }
`;

const NewsAggregator: React.FC = () => {
  const [category, setCategory] = useState("technology");
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [showSearchInput, setShowSearchInput] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(false);

  const { data: news = [], isLoading: loading, error } = useQuery({
    queryKey: ["news", category, searchTerm, currentPage],
    queryFn: async ({ queryKey }) => {
      const [, category, searchTerm, currentPage] = queryKey;
      const BACKEND_URL =
        process.env.NODE_ENV === "development"
          ? "http://localhost:5001"
          : process.env.REACT_APP_BACKEND_URL;

      const url = searchTerm
        ? `${BACKEND_URL}/api/news?search=${searchTerm}&page=${currentPage}`
        : `${BACKEND_URL}/api/news?category=${category}&page=${currentPage}`;
      const response = await axios.get(url);
      return response.data.articles || [];
    },
    staleTime: 5 * 60 * 1000,
    refetchOnWindowFocus: false,
  });

  const handleCategoryButtonClick = (newCategory: string) => {
    if (newCategory !== category) {
      setCategory(newCategory);
      setCurrentPage(1);
    }
  };

  const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchTerm(e.target.value);
  };

  const toggleTheme = () => {
    setIsDarkMode((prev) => !prev);
  };

  return (
    <ThemeProvider theme={isDarkMode ? darkTheme : lightTheme}>
      <GlobalStyle />
      <Container>
        <Navbar>
          <Logo>
            <FaNewspaper size={24} />
            News Aggregator
          </Logo>
          <NavLinks>
            {["technology", "business", "entertainment", "health", "science", "sports"].map((cat) => (
              <NavButton key={cat} onClick={() => handleCategoryButtonClick(cat)} $active={category === cat}>
                {cat.charAt(0).toUpperCase() + cat.slice(1)}
              </NavButton>
            ))}
            {showSearchInput ? (
              <SearchBox
                type="text"
                placeholder="Search..."
                value={searchTerm}
                onChange={handleSearchChange}
                onBlur={() => setShowSearchInput(false)}
              />
            ) : (
              <SearchIconContainer onClick={() => setShowSearchInput(true)}>
                <FaSearch />
              </SearchIconContainer>
            )}
            <ToggleIconContainer onClick={toggleTheme}>
              {isDarkMode ? <FaMoon /> : <FaSun />}
            </ToggleIconContainer>
          </NavLinks>
        </Navbar>
        {error ? (
          <p style={{ textAlign: "center", color: "#4b5563" }}>{error.message}</p>
        ) : loading ? (
          <p style={{ textAlign: "center", color: "#4b5563" }}>Loading news...</p>
        ) : (
          <>
            <NewsGrid>
              {news.map((article: any, index: any) => (
                <NewsCard key={index}>
                  {article.urlToImage && <img src={article.urlToImage} alt={article.title} />}
                  <h2>{article.title}</h2>
                  <p>{article.source.name}</p>
                  <p>{article.description}</p>
                  <p style={{ color: article.isFake ? "red" : "green", fontWeight: "bold" }}>
                    {article.isFake ? "⚠️ Fake News" : "✅ Real News"}
                  </p>
                  <a href={article.url} target="_blank" rel="noopener noreferrer">
                    Read more
                  </a>
                </NewsCard>
              ))}
            </NewsGrid>

            <Pagination>
              <PageButton
                onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
                disabled={currentPage === 1}
              >
                Previous
              </PageButton>
              <PageButton onClick={() => setCurrentPage((prev) => prev + 1)}>Next</PageButton>
            </Pagination>
          </>
        )}
      </Container>
    </ThemeProvider>
  );
};

export default NewsAggregator;
